query:
//Get movies released after 2009 and their casts
MATCH (a:Person)-[:ACTED_IN]->(m:Movie)
WHERE m.released >= 2009
WITH m, COLLECT(a) AS cast

//Find other movies that contain all actors from that cast
MATCH (a2:Person)-[:ACTED_IN]->(m2:Movie)
WITH m, cast, m2, COLLECT(a2) AS cast2
WHERE ALL(actor IN cast WHERE actor IN cast2)

//Return results
RETURN m.title AS OriginalMovie, m2.title AS OtherMovie, [actor IN cast | actor.name] AS Cast
